import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState(null);
  const [chatMessage, setChatMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Scan folders on component mount
    scanFolders();
  }, []);

  const scanFolders = async () => {
    try {
      const response = await axios.get('http://localhost:8000/scan-folders');
      setFiles(response.data.files);
    } catch (error) {
      console.error('Error scanning folders:', error);
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);

    if (file) {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await axios.post('http://localhost:8000/process-pdf', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setFileContent(response.data);
      } catch (error) {
        console.error('Error uploading file:', error);
      }
    }
  };

  const sendMessage = async () => {
    if (!chatMessage.trim()) return;

    const newMessage = { type: 'user', content: chatMessage };
    setChatHistory([...chatHistory, newMessage]);

    try {
      const response = await axios.post('http://localhost:8000/chat', {
        message: chatMessage,
        context: fileContent?.text || ''
      });

      setChatHistory(prev => [...prev, {
        type: 'bot',
        content: response.data.response
      }]);
    } catch (error) {
      console.error('Error sending message:', error);
    }

    setChatMessage('');
  };

  return (
    <div className="App">
      <div className="container">
        <div className="file-explorer">
          <h2>Legal Documents</h2>
          <div className="file-list">
            {files.map((file, index) => (
              <div 
                key={index} 
                className={`file-item ${selectedFile?.path === file.path ? 'selected' : ''}`}
                onClick={() => handleFileUpload({ target: { files: [file] } })}
              >
                <span>{file.filename}</span>
                <small>{file.folder}</small>
              </div>
            ))}
          </div>
        </div>

        <div className="content-viewer">
          {loading ? (
            <div className="loading">Processing...</div>
          ) : fileContent ? (
            <>
              <h3>Document Summary</h3>
              <div className="summary">{fileContent.summary}</div>
              <h3>Preview</h3>
              <div className="preview">{fileContent.text}</div>
            </>
          ) : (
            <div className="no-selection">Select a file to view</div>
          )}
        </div>

        <div className="chat-section">
          <div className="chat-history">
            {chatHistory.map((msg, index) => (
              <div key={index} className={`chat-message ${msg.type}`}>
                {msg.content}
              </div>
            ))}
          </div>
          <div className="chat-input">
            <input
              type="text"
              value={chatMessage}
              onChange={(e) => setChatMessage(e.target.value)}
              placeholder="Ask a question about the document..."
              onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
